mapper.select:
SELECT staff.ID AS id, staff.EMAIL AS email, staff.PASSWORD AS password, staff.NAME AS name, staff.ROLE AS role FROM m_staff staff
AND staff.id = #{id}
AND staff.EMAIL = #{email}
AND staff.DEL_FLG = '0'

mapper.insert:
INSERT INTO m_staff (
    email
,   password
,   NAME
,   role
) VALUES (
    #{email}
,   #{password}
,   #{name}
,   #{role}
)

    SELECT @@IDENTITY

mapper.update:
UPDATE m_staff
EMAIL = #{email}, NAME = #{name}, PASSWORD = #{password}, ROLE = #{role},
WHERE ID = #{id}
